/***
 * 10-27-2022 - Leo
 * Adding 2nd and 3rd modal box and button. This is a very quick solution because of the limited time
 * Need to refactor code to remove the repeating variable and event listener calls for each modal. Did this 
 * solution to ensure the modal works with the PDF and separated the variables to avoid conflicts.
 * */

// When the user clicks the button, open the modal 
btn2.onclick = function() {  
  modal2.style.display = "block";
}
// When the user clicks the button, open the modal 
btn3.onclick = function() {  
  modal3.style.display = "block";
}

// When the user clicks on <span> (x), close the modal
span2.onclick = function() {
  modal2.style.display = "none";
}
// When the user clicks on <span> (x), close the modal
span3.onclick = function() {
  modal3.style.display = "none";
}

// When the user clicks anywhere outside of the modal, close it
window.onclick = function(event) {
  // if (event.target == modal) {
  //   modal.style.display = "none";
  // }

  if (event.target == modal2) {
    modal2.style.display = "none";
  }

  if (event.target == modal3) {
    modal3.style.display = "none";
  }
}

//if mobile, do not open PDF if modal box, open in new tab
if( /Android|webOS|iPhone|iPad|iPod|BlackBerry|IEMobile|Opera Mini/i.test(navigator.userAgent) ) {

  btn2.onclick = function(e) {  
    e.preventDefault();
    let reviewBook = document.getElementById('reviews-ebook');
    window.open(reviewBook.getAttribute('data'), '_blank');
  }

  btn3.onclick = function(e) {  
    e.preventDefault();
    let referralBook = document.getElementById('referral-ebook');
    window.open(referralBook.getAttribute('data'), '_blank');
  }
}

</script>

